import React, { useRef, useState } from 'react'
import { Link } from 'react-router-dom'
import './Navbar.css'
import { Bio } from '../../data/Constants'

const Navbar = () => {
 
 const Skills = useRef(null)
 
 const scrollToComponent = (elementRef) =>{
    window.scrollTo({
      top: elementRef.current.offsetTop,
      behavior : 'smooth'

    })
 }
 
 
 
 
  const [color , setColor] = useState(false)
 const changeColor = () =>{
     if(window.scrollY >=100){
        setColor(true)
      }
      else{
        setColor(false)
      }
 }

 window.addEventListener("scroll" ,changeColor)
 
  return (
    
       <div className={color ? "nav nav-bg" : "nav"}>
          <div className='nav-container'>
             <div className='logo'>
               <Link to='/' className='linkk'>
                 <h1 className='title'><span className='A'>A</span>G</h1>
               </Link>
             </div>

             <div className='menu'>
               <ul className="nav-tabs">
                 <li><Link to="/" className='underline'>Home</Link></li>
                 <li><Link to='/aboutUs' className='underline'>About Us</Link></li>
                 <li><Link  to='/education'  className='underline'>Education</Link></li>
                 <li><Link to='/experience' className='underline'>Experience</Link></li>
                 <li><Link to='/skill' className='underline'>Skills</Link></li>
                 <li><Link to='/projects' className='underline'>Projects</Link></li>
               </ul>
             </div>

             <div className='button-Container'>
                <a href={Bio.github} target="_blank" className='underline'><button className='btn'>Github Profile</button></a>
             </div>
          </div>
       </div>
   
  )
}

export default Navbar
